import React from 'react';
import { useState, useEffect } from 'react';

const ApprouverOuSupprimerAvis = () => {

    const [selectListeAvisName, setSelectListeAvisName] = useState('');
    const [listeAvis, setListeAvis] = useState([]);
    //const [array, setArray] = useState([]);

    //const avisNonApprouve = JSON.parse(localStorage.getItem('liste des avis non approuvés'));
    //console.log('dashboardEmployer', avisNonApprouve)

    useEffect(() => {
        const avisNonApprouve = JSON.parse(localStorage.getItem('liste des avis non approuvés'));
        setListeAvis(avisNonApprouve);
    }, [selectListeAvisName]);

    const supprimerAvis = (/*e*/) => {
        //e.preventDefault();

        /*setArray([...array, selectListeAvisName]);

        if (listeAvis.includes(array)) {

        }*/


        const updateListeAvis = listeAvis.filter(avis => avis.name !== selectListeAvisName);

        localStorage.setItem('liste des avis non approuvés', JSON.stringify(updateListeAvis));

        setListeAvis(updateListeAvis);

        alert(`L'avis de ${selectListeAvisName} à bien été supprimer`)
    }

    console.log('avis name change ou pas', selectListeAvisName)

    return (
        <div className="listeAvis">
            <h2>Liste des avis en attente de confirmation</h2>
            {listeAvis.map((avis, index) => (
                <div className="avis" index={index}>
                    <div className="nomClientNote">
                        <h4>Avis numéro {index + 1}</h4>
                        <h4>
                            {avis.name}
                        </h4>
                        <h4>{avis.note}/5</h4>
                        {/*<input
                            type="checkbox"
                            name="seletionAvis"
                            id="selectionAvis"
                            //value={selectListeAvisName}
                            onClick={(e) => setSelectListeAvisName(avis.name)}
            />*/}
                    </div>
                    <p>{avis.message}</p>
                    {/*<button>Approuver</button>
                    <button
                        value={selectAvisName}
                        onClick={supprimerAvis}
                        onChange={(e) => setSelectAvisName(index + 1)}
                    >
                        Supprimer
            </button>*/}
                    <button
                        type="button"
                        id="buttonSupprimerAvis"
                        name="buttonSupprimerAvis"
                        value={selectListeAvisName}
                        onClick={() => {
                            setSelectListeAvisName(avis.name);
                            setTimeout(supprimerAvis, 2000);
                        }}

                    >
                        Supprimer
                    </button>
                </div>
            ))
            }
        </div >
    );
};

export default ApprouverOuSupprimerAvis;
