import React from 'react';
import ford from "./image/voiture_ford_1.jpg"
import honda from "./image/voiture_honda_2.jpg"
import audi from "./image/voiture_audi_1.png"
import renault from "./image/voiture_renault_1.jpg"
import peugeot from "./image/voiture_peugeot_1.jpg"
import FiltreVoiture from './FiltreVoiture';

const BlocVoitures = () => {
    /*
        const [listeVoitures, setListeVoitures] = useState([]);
    
        useEffect(() => {
            const fetchVoitures = async () => {
                try {
                    const response = await axios.get("https://public.opendatasoft.com/api/explore/v2.1/catalog/datasets/all-vehicles-model/records?limit=8");
                    setListeVoitures(Array.from(response.data.results));
                } catch (error) {
                    console.error("Erreur lors de la récupération des voitures", error);
                }
            };
            fetchVoitures();
        }, []);*/

    const listeVoitures = ([
        { id: "1", km: 214000, price: "5 000", yearsCirculation: "2005", image: ford },
        { id: "2", km: 139000, price: "7 000", yearsCirculation: "2002", image: honda },
        { id: "3", km: 56000, price: "9 000", yearsCirculation: "2021", image: audi },
        { id: "4", km: 189000, price: "4 500", yearsCirculation: "2011", image: renault },
        { id: "5", km: 23000, price: "12 000", yearsCirculation: "1998", image: peugeot },
        { id: "5", km: 13000, price: "12 000", yearsCirculation: "1998", image: peugeot }
    ]);


    return (
        <div>
            <FiltreVoiture listeVoitures={listeVoitures} />

            <div className="listeDeVoitures">
                {listeVoitures.map((voiture, index) => (
                    <div className="Voiture" key={index}>
                        <img className="imageVoiture" src={voiture.image} />
                        <p>Id : {index + 1}</p>
                        <p>Kilométrage : {voiture.km} km</p>
                        <p>Prix : {voiture.price} €</p>
                        <p>Mise en circulation en : {voiture.yearsCirculation}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BlocVoitures;
