import React from 'react';
import ReactModal from 'react-modal';
import { useState, useEffect } from 'react';

const BouttonSupprimerServices = () => {

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectService, setSelectService] = useState('');

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    const getListeServicesUpdate = JSON.parse(localStorage.getItem('liste de services update'));
    console.log('bouton supprimer', getListeServicesUpdate);

    getListeServicesUpdate.map(service => {
        console.log(service.name);
    });

    console.log('boutton supprimer 2', selectService);

    return (
        <div>
            <button className="buttonSupprimerService" onClick={openModal}>Supprimer un service</button>
            <ReactModal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
            >
                <form className="supprimerService">
                    <legend>Quel service souhaitez-vous supprimer ?</legend>
                    <select
                        name="services"
                        value={selectService}
                        onChange={(e) => setSelectService(e.target.value)}
                        required
                    >
                        <option value="">Veuillez choisir le service à supprimer</option>
                        {getListeServicesUpdate.map((service, index) => (
                            <option key={index} value={service.name}>
                                {service.name}
                            </option>
                        ))}
                    </select>
                    <button >Supprimer</button>
                    <button onClick={closeModal}>Annuler</button>
                </form>

            </ReactModal>
        </div>
    );
};

export default BouttonSupprimerServices;
