import React from 'react';
import StarRating from './StarRating';
import { useState } from 'react';
import AjouterAvis from './AjouterAvis';

const Avis = () => {

    const [donnerAvis, setDonnerAvis] = useState(false);
    const [nom, setNom] = useState('');
    const [avis, setAvis] = useState('');

    const note = localStorage.getItem('note clients');
    console.log(note);

    const updateListeAvis = JSON.parse(localStorage.getItem('liste des avis'));
    console.log('page avis', updateListeAvis)

    const envoyerAvis = () => {
        alert('Votre avis à bien été envoyé')
    }

    return (
        <div className="Avis">
            Avis des clients
            <button className="donnerAvis" onClick={() => setDonnerAvis(true)}>Donner son avis</button>
            {donnerAvis === false ?
                <div className="listeAvis">
                    {updateListeAvis.map((avis, index) => (
                        <div className="avis" index={index}>
                            <div className="nomClientNote">
                                <h4>{avis.name}</h4>
                                <h4>{avis.note}/5</h4>
                            </div>
                            <p>{avis.message}</p>
                        </div>
                    ))}
                </div>
                :
                <AjouterAvis />
                /*
                <div>
                    <form>
                        <legend>Laissez votre avis</legend>

                        <input
                            type="text"
                            name="nom"
                            id="nom"
                            placeholder="Votre nom"
                            value={nom}
                            onChange={(e) => setNom(e.target.value)}
                        />
                        <label htmlFor="nom"></label>

                        <p>Veuilez saisir votre note</p>
                        <StarRating />

                        <textarea
                            name="avis"
                            id="avis"
                            cols="150"
                            rows="5"
                            placeholder="Votre message"
                            value={avis}
                            onChange={(e) => setAvis(e.target.value)}
                        />
                    </form>
                    <button onClick={() => (envoyerAvis(), setDonnerAvis(false))}>Confimer</button>
                </div>*/
            }
        </div>
    );
};

export default Avis;
