import React from 'react';
import { FaStar } from "react-icons/fa";
import { useState } from 'react';

const AjouterAvis = () => {

    const [newAvis, setNewAvis] = useState(getDefaultAvis());
    const [donnerAvis, setDonnerAvis] = useState(false);

    const noteClient = localStorage.getItem('note clients');
    console.log('ajouter Avis', noteClient)

    function getDefaultAvis() {
        return {
            name: "Tibo",
            note: 4,
            message: "Lorem ipsum"
        };
    };

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;

        const newValue = type === "radio" ? noteClient : value;

        setNewAvis({
            ...newAvis,
            [name]: newValue,
        });
    };

    const handleAddAvis = () => {
        const getListeAvisUpdate = JSON.parse(localStorage.getItem('liste des avis')) || [];
        const updateAvis = [...getListeAvisUpdate, newAvis];
        localStorage.setItem('liste des avis', JSON.stringify(updateAvis));
        setNewAvis(getDefaultAvis());
        alert('Votre avis à bien été envoyé');
        setDonnerAvis(false);
    };

    return (
        <div>
            <button className="donnerAvis" onClick={() => setDonnerAvis(true)}>Donner son avis</button>
            {donnerAvis === false ?
                null
                :
                <div>
                    <form onSubmit={handleAddAvis}>
                        <legend>Laissez votre avis</legend>

                        <input
                            type="text"
                            name="name"
                            id="name"
                            placeholder="Votre nom"
                            onChange={handleInputChange}
                            required
                        />
                        <label htmlFor="name"></label>

                        <p>Veuilez saisir votre note</p>

                        <div>
                            {[...Array(5)].map((star, index) => {
                                const ratingValue = index + 1;

                                return (
                                    <div className="positionStarRating">
                                        <label>
                                            <input
                                                type="radio"
                                                name="rating"
                                                value={ratingValue}
                                                onClick={() => setRating(ratingValue)}
                                            />
                                            <FaStar
                                                className="star"
                                                color={ratingValue <= (hover || rating) ? "#ffc107" : "#e4e5e9"}
                                                size={40}
                                                onMouseEnter={() => setHover(ratingValue)}
                                                onMouseLeave={() => setHover(null)}
                                            />
                                        </label>
                                    </div>
                                );
                            })}
                            <p>la note est {rating}</p>
                        </div>

                        <textarea
                            name="message"
                            id="message"
                            cols="150"
                            rows="5"
                            placeholder="Votre message"
                            onChange={handleInputChange}
                        />
                        <label htmlFor="message"></label>
                        <button type="submit">Confimer</button>
                    </form>
                </div>
            }
        </div >
    );
};

export default AjouterAvis;
