import React from 'react';
import { useEffect, useState } from 'react';
import voitureDefault from "./image/voiture-mystere.jpg"

const BoutonAjoutVoitures = () => {

    const [newVoitures, setNewVoitures] = useState(getDefaultVoiture());
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    useEffect(() => {
        //Récupérer les données update du localStorage
        const voituresExisting = JSON.parse(localStorage.getItem('liste de voitures')) || [];
    }, []);

    function getNextRecetteId() {
        const existingMeals = JSON.parse(localStorage.getItem('filteredMeals')) || [];
        const lastRecette = existingMeals[existingMeals.length - 1];
        return lastRecette ? lastRecette.id + 1 : 1;
    }


    function getDefaultVoiture() {
        return {
            id: getNextId(),
            brand: "marque par default",
            km: 10000,
            price: 15000,
            yearsCirculation: 2022,
            image: voitureDefault,
        };
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        const newValue = value;

        setNewServices({
            ...newVoitures,
            [name]: newValue,
        });
    };

    const handleAddVoiture = () => {
        const voituresExisting = JSON.parse(localStorage.getItem('liste de voitures')) || [];
        const updateVoitures = [...voituresExisting, newVoitures];
        localStorage.setItem('liste de voitures', JSON.stringify(updateVoitures));
        setNewVoitures(getDefaultService());
        setIsModalOpen(false);
    }

    const handleImageChange = (e) => {
        if (e.target.files && e.target.files[0]) {
            const img = e.target.files[0];
            setNewVoitures({
                ...newVoitures,
                image: URL.createObjectURL(img)
            })
        }
    };

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div>
            <button className="buttonAjouterVoiture" onClick={openModal}>Ajouter une voiture</button>
        </div>
    );
};

export default BoutonAjoutVoitures;
