import React from 'react';
import { useState, useEffect } from 'react';

const BoutonFermeOuvert = () => {

    const [action, setAction] = useState('ouvert');

    useEffect(() => {
        localStorage.setItem('ouvert ou fermer', action);
    }, []);

    const ouvertOuFermer = localStorage.getItem('ouvert ou fermer');
    console.log(ouvertOuFermer);

    return (
        <div>
            {action === 'ouvert' ?
                <button className="OuvertOuFermer" onClick={() => setAction('fermer')}>{action}</button>
                :
                <button className="OuvertOuFermer" onClick={() => setAction('ouvert')}>{action}</button>
            }
        </div >
    );
};

export default BoutonFermeOuvert;
