import axios from 'axios';
import React, { useEffect, useState } from 'react';
import ford from "./image/voiture_ford_1.jpg"
import honda from "./image/voiture_honda_2.jpg"

const BlocVoitures = () => {

    const [listeVoitures, setListeVoitures] = useState([]);

    useEffect(() => {
        const fetchVoitures = async () => {
            try {
                const response = await axios.get("https://public.opendatasoft.com/api/explore/v2.1/catalog/datasets/all-vehicles-model/records?limit=10");
                setListeVoitures(Array.from(response.data.results));
            } catch (error) {
                console.error("Erreur lors de la récupération des voitures", error);
            }
        };
        fetchVoitures();
    }, []);

    const voituresData = ([
        { id: "1", name: "ford", image: ford },
        { id: "2", name: "ford", image: honda },
        { id: "3", name: "ford", image: ford },
        { id: "4", name: "ford", image: ford },
        { id: "5", name: "ford", image: ford },
        { id: "6", name: "ford", image: ford },
        { id: "7", name: "ford", image: ford },
        { id: "8", name: "ford", image: ford }
    ]);

    return (
        <div>
            <div className="listeDeVoitures">
                {listeVoitures.map((voitures, index) => (
                    <div className="Voiture" key={index}>
                        <p>numéro : {index + 1}</p>
                        <p>Id : {voitures.id}</p>
                        <p>marque : {voitures.make}</p>
                        <p>Année : {voitures.createdon}</p>
                        <p>Prix : {voitures.fuelcost08}</p>
                        <p>Kilométrage : {Math.round(voitures.highway08)} 000 km</p>
                        <img src={voituresData.image} />


                        {/*
                        {console.log('voitures.id ' + voitures.id)}
                        {console.log('voituresData.id ' + voituresData.id)}
                        */}

                        {/*
                        {voituresData.map((photo, index) => {
                            { console.log('voituresData.id ' + voituresData[index].id) }
                        })}
                    */}


                    </div>
                ))}
            </div>
        </div>
    );
};

export default BlocVoitures;
