import React from 'react';
import { useState } from 'react';

const FiltreVoiture = ({ listeVoitures }) => {

    const [kilometrage, setKilometrage] = useState('');

    const filterKilometrage = Array.from(
        new Set(listeVoitures.map(voiture => voiture.km))
    )

    const minKilometrage = Math.min(...filterKilometrage);

    const maxKilometrage = Math.max(...filterKilometrage);

    const updateKilometrage = (e) => {
        const { value } = e.target;

        setKilometrage(value);
    };

    const filtreVoituresKm = listeVoitures.filter(
        voiture => voiture.km >= minKilometrage && voiture.km <= maxKilometrage
    )

    console.log(filtreVoituresKm)

    return (
        <div className="FiltreVoiture">
            <div>
                <label htmlFor="Kilometrage">Kilométrage</label>
                <input
                    type="range"
                    name="kilometrage"
                    min={minKilometrage}
                    max={maxKilometrage}
                    value={kilometrage}
                    onChange={updateKilometrage}
                />
                <span>{minKilometrage} - {kilometrage} km</span>
            </div>
        </div>
    );
};

export default FiltreVoiture;
