import React from 'react';
import { useParams } from 'react-router-dom';
import Navigation from '../components/Navigation';
import { useState, useEffect } from 'react';

const DetailsVoiture = () => {

    const { id } = useParams();
    const [dataVoitures, setDataVoitures] = useState({});

    useEffect(() => {
        const voituresExisting = JSON.parse(localStorage.getItem('liste de voitures')) || [];

        const selectedVoiture = voituresExisting.find(voiture => voiture.id === id);

        if (selectedVoiture) {
            setDataVoitures(selectedVoiture);
        } else {
            console.error(`La voiture avec l'ID ${id} est introuvable`)
        }
    }, [id]);


    return (
        <div>
            <Navigation />
            <div className="grosConteneurDetailVoiture">
                <h2>Annonce pour la voiture {dataVoitures.brand}</h2>
                <div className="petitConteneurDetailVoiture">

                    <img className="imageDetailVoiture" src={dataVoitures.image} />
                    <div>
                        <p>Id : {dataVoitures.id}</p>
                        <p>Kilométrage : {dataVoitures.km} km</p>
                        <p>Prix : {dataVoitures.price} €</p>
                        <p>Mise en circulation en : {dataVoitures.yearsCirculation}</p>
                        <span>Intéressé ? contactez-nous par message ou par téléphone</span>
                        <div className="boutonContactVoiture">
                            <button className="boutonContact">Par message</button>
                            <button className="boutonContact">Par téléphone</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default DetailsVoiture;
