import React, { useState } from 'react';
import ReactModal from 'react-modal';
import axios from 'axios';

const BoutonCreerUtilisateur = () => {

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [newUser, setNewUser] = useState(getDefaultUser());

    function getDefaultUser() {
        return {
            username: "",
            email: "",
            password: "",
            role: "utilisateur"
        }
    };

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        setNewUser({
            ...newUser,
            [name]: value,
        })
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3001/CreateUser', newUser, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            console.log('Succès:', response.data);
            setNewUser(getDefaultUser());
            setIsModalOpen(false);
        } catch (error) {
            console.error('Erreur:', error.response ? error.response.data : error.message);
        }
    }

    return (
        <div>
            <button className="boutonCreerUtilisateur" onClick={openModal}>Créer un utilisateur</button>

            <ReactModal isOpen={isModalOpen} onRequestClose={closeModal}>
                <form onSubmit={handleSubmit}>
                    <legend>Créer un utilisateur</legend>
                    <input
                        type="text"
                        name="username"
                        placeholder="Nom d'utilisateur"
                        onChange={handleInputChange}
                        value={newUser.username}
                        required
                    />

                    <input
                        type="email"
                        name="email"
                        placeholder="Email"
                        onChange={handleInputChange}
                        value={newUser.email}
                        required
                    />

                    <input
                        type="password"
                        name="password"
                        placeholder="Mot de passe"
                        onChange={handleInputChange}
                        value={newUser.password}
                        required
                    />

                    <select
                        name="role"
                        onChange={handleInputChange}
                        value={newUser.role}
                    >
                        <option value="utilisateur">Utilisateur</option>
                        <option value="admin">Administrateur</option>
                    </select>

                    <div>
                        <button type="submit">Créer</button>
                        <button type="button" onClick={closeModal}>Annuler</button>
                    </div>
                </form>
            </ReactModal>
        </div>
    );
};

export default BoutonCreerUtilisateur;
