import React from 'react';
import Navigation from '../components/Navigation';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Footer from '../components/Footer';
import axios from 'axios';

const SeConnecter = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    //const [estConnecte, setEstConnecte] = useState(false);
    const [listeEmployes, setListeEmployes] = useState('');

    const navigate = useNavigate();

    /*const connexion = (e) => {
        e.preventDefault();
        console.log('bravo vous êtes connecté', email, password);
        localStorage.setItem('email user', email);
        localStorage.setItem('password user', password);
        const emailUser = localStorage.getItem('email user');
        const passwordUser = localStorage.getItem('password user');

        if ((emailUser === "vincent@vincent" && passwordUser === "parrot") ||
            (emailUser === "employe@employe" && passwordUser === "employe")) {
            navigate("/");
            window.location.reload();
        } else {
            alert('Vous n\'êtes pas autorisé à utiliser l\'espace pro');
        }
    }*/

    useEffect(() => {
        const getEmployes = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/get', {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                const employes = JSON.stringify(response.data)
                setListeEmployes(Array.from(new Set(JSON.parse(employes))));
                console.log('Succès:', response.data);
            } catch (error) {
                console.error('Erreur:', error.response ? error.response.data : error.message);
            }
        }
        getEmployes();
    }, []);

    const tableauEmployes = Array.from(
        new Set(listeEmployes)
    );

    const connexion = (e) => {
        e.preventDefault();
        tableauEmployes.map(employe => {
            if ((employe.email === email && employe.password === password)) {
                localStorage.setItem('etat connexion', true);
                console.log('Bravo vous êtes connecté', email, ' ', password);
                navigate("/");
            } else if (email === "vincent@vincent" && password === "parrot") {
                localStorage.setItem('etat connexion', true);
                localStorage.setItem('compte admin', true);
                navigate("/");
            } else {
                console.log('Compte employé introuvable');
            }
        })
    };

    const deconnexion = (e) => {
        e.preventDefault();
        setEmail('');
        setPassword('');
        localStorage.removeItem('etat connexion');
        if (localStorage.getItem('compte admin')) {
            localStorage.removeItem('compte admin');
        }
        navigate("/");
    }

    console.log(localStorage.getItem('etat connexion'))

    const etatConnexion = localStorage.getItem('etat connexion');

    return (
        <div>
            <Navigation />
            <h1>Espace professionnel</h1>
            <div className="conteneurConnexion">
                <form className="formulaireConnexion">
                    {localStorage.getItem('etat connexion') === true ?
                        <div>
                            <legend>Vous êtes connecté en tant que {email}</legend>
                            <button onClick={deconnexion}>
                                Se déconnecter
                            </button>
                        </div>
                        :
                        <div>
                            <input
                                type="email"
                                name="email"
                                id="email"
                                placeholder="votre email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                            <label htmlFor="email"></label>

                            <input
                                type="password"
                                name="password"
                                id="password"
                                placeholder="votre mot de passe"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                            <label htmlFor="password"></label>

                            <div className="boutonConnexion">
                                <button onClick={connexion}>
                                    Se connecter
                                </button>
                            </div>
                        </div>
                    }
                </form>
            </div>
            <Footer />
        </div>
    );
};

export default SeConnecter;
