import React from 'react';
import { useEffect, useState } from 'react';
import axios from 'axios';

const BoutonSupprimerEmploye = () => {

    const [listeEmployes, setListeEmployes] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    useEffect(() => {
        const getEmployes = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/get', {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                const employes = JSON.stringify(response.data)
                setListeEmployes(Array.from(new Set(JSON.parse(employes))));
                console.log('Succès:', response.data);
            } catch (error) {
                console.error('Erreur:', error.response ? error.response.data : error.message);
            }
        }
        getEmployes();
    }, []);

    const tableauEmployes = Array.from(
        new Set(listeEmployes)
    );

    const handleSubmit = async (e) => {
        console.log('ok ok')
        e.preventDefault();
        setIsModalOpen(false);
    }

    return (
        <div>
            <button className="boutonSupprimerEmploye" onClick={openModal}>Supprimer compte employé</button>
        </div>
    );
};

export default BoutonSupprimerEmploye;
