import React from 'react';
import ReactModal from 'react-modal';
import { useState, useEffect } from 'react';

const Footer = () => {

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [date, setDate] = useState("");
    const [creneau, setCreneau] = useState("");
    const [heure, setHeure] = useState("");

    const heureMatin = ["08h45 - 09h00", "09h00 - 09h15", "09h15 - 09h30", "09h30 - 09h45",
        "09h45 - 10h00", "10h00 - 10h15", "10h15 - 10h30", "10h30 - 10h45", "10h45 - 11h00",
        "11h00 - 11h15", "11h15 - 11h30", "11h30 - 11h45", "11h45 - 12h00"];

    const heureMidi = ["14h00 - 14h15", "14h15 - 14h30", "14h30 - 14h45", "14h45 - 15h00",
        "15h00 - 15h15", "15h15 - 15h30", "15h30 - 15h45", "15h45 - 16h00", "16h00 - 16h15",
        "16h15 - 16h30", "16h30 - 16h45", "16h45 - 17h00", "17h00 - 17h15", "17h15 - 17h30",
        "17h30 - 17h45", "17h45 - 18h00"];

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    const dateRendezVous = new Date(date).getDay();

    const priseRdv = (e) => {
        e.preventDefault();
        alert("Vous avez pris rendez-vous pour le " + date + " Ã  " + heure);
        closeModal();
    }

    return (
        <div className="Footer">
            <div className="horaireGarage">
                <table>
                    <div>
                        <tr>
                            <td>Lun :</td>
                            <td>08h45 - 12h00, 14h00 - 18h00</td>
                        </tr>
                        <tr>
                            <td>Mar :</td>
                            <td>08h45 - 12h00, 14h00 - 18h00</td>
                        </tr>
                    </div>
                    <div>
                        <tr>
                            <td>Mer :</td>
                            <td>08h45 - 12h00, 14h00 - 18h00</td>
                        </tr>
                        <tr>
                            <td>Jeu :</td>
                            <td>08h45 - 12h00, 14h00 - 18h00</td>
                        </tr>
                    </div>
                    <div>
                        <tr>
                            <td>Ven :</td>
                            <td>08h45 - 12h00, 14h00 - 18h00</td>
                        </tr>
                        <tr>
                            <td>Sam :</td>
                            <td>08h45 - 12h00</td>
                        </tr>
                    </div>
                    <div>
                        <tr>
                            <td>Dim :</td>
                            <td>FermÃ©</td>
                        </tr>
                    </div>
                    <div>
                        <button onClick={openModal}>Je prends rendez-vous</button>
                    </div>
                </table>
            </div>
            <ReactModal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
                className="modalRdv"
            >
                <form onSubmit={priseRdv} className="priseRdv">
                    <div>
                        <legend>
                            Votre rendez-vous
                        </legend>
                        <input type="text" name="nom" id="nom" placeholder="Votre nom" required></input>
                        <label htmlFor="nom"></label>

                        <input type="text" name="prenom" id="prenom" placeholder="Votre prÃ©nom" required></input>
                        <label htmlFor="prenom"></label>

                        <input type="email" name="email" id="email" placeholder="Votre email" required></input>
                        <label htmlFor="email"></label>

                        <input type="tel" name="phone" id="phone" placeholder="Votre numÃ©ro de tÃ©lÃ©phone" required></input>
                        <label htmlFor="phone"></label>

                        <input type="date" id="date" name="date" min="2024-01-01" max="2024-12-31" onChange={e => setDate(e.target.value)} required></input>
                        <label htmlFor="date"></label>

                        <div>
                            {dateRendezVous === 0 ? null
                                :
                                dateRendezVous === 6 ?
                                    null
                                    :
                                    <label htmlFor="creneau" id="creneau">Choisissez votre crÃ©neau horaire
                                        <input type="radio" id="creneau" name="creneau" onChange={(e) => setCreneau("Matin")} required></input><span>Matin</span>
                                        <input type="radio" id="creneau" name="creneau" onChange={(e) => setCreneau("AprÃ¨s-midi")} required></input><span>AprÃ¨s-midi</span>
                                    </label>
                            }

                            {creneau === "Matin" || dateRendezVous === 6 ?
                                <select
                                    name="heureRendezVous"
                                    id="heureRendezVous"
                                    value={heure}
                                    onChange={(e) => setHeure(e.target.value)}
                                    required
                                >
                                    <option value="">Veuillez choisir votre heure de rendez-vous</option>
                                    {heureMatin.map((horaire, index) => (
                                        <option key={index} value={horaire}>
                                            {horaire}
                                        </option>
                                    ))}
                                </select>
                                :
                                creneau === "AprÃ¨s-midi" ?
                                    <select
                                        name="heureRendezVous"
                                        id="heureRendezVous"
                                        value={heure}
                                        onChange={(e) => setHeure(e.target.value)}
                                        required
                                    >
                                        <option value="">Veuillez choisir votre heure de rendez-vous</option>
                                        {heureMidi.map((horaire, index) => (
                                            <option key={index} value={horaire}>
                                                {horaire}
                                            </option>
                                        ))}
                                    </select>
                                    :
                                    null
                            }
                            <div>
                                <button type="submit" id="confirmer">Confirmer</button>
                                <button id="annuler" onClick={closeModal}>Annuler</button>
                            </div>
                        </div>
                    </div>
                </form>
            </ReactModal>
        </div>
    );
};

export default Footer;
