import React from 'react';
import AjouterAvis from './AjouterAvis';
import { FaStar } from "react-icons/fa";
import { useState } from 'react';

const Avis = () => {

    const updateListeAvis = JSON.parse(localStorage.getItem('liste des avis'));

    return (
        <div className="Avis">
            Avis des clients
            <AjouterAvis />
            <div className="listeAvis">
                {updateListeAvis.map((avis, index) => (
                    < div className="avis" index={index} >
                        {() => setNoteStar(parseInt(avis.note))}
                        <div className="nomClientNote">
                            <h4>{avis.name}</h4>
                            <h4>{avis.note}/5</h4>
                            <div>
                                {[...Array(avis.note)].map((star, index) => {
                                    //const ratingValue = index + 1;
                                    console.log(typeof avis.note)
                                    return (
                                        <div className="positionStarRating">
                                            <input
                                                type="radio"
                                                name="note"
                                                id="note"
                                                value={noteStar}
                                            />
                                            <label htmlFor="note" />

                                            <FaStar
                                                value={noteStar}
                                                size={40}
                                            />
                                        </div>
                                    );
                                })}
                            </div>
                        </div>
                        <p>{avis.message}</p>
                    </div>
                ))}
            </div>
        </div >
    );
};

export default Avis;
