import React from 'react';
import AjouterAvis from './AjouterAvis';
import { FaStar } from "react-icons/fa";

const Avis = () => {

    const updateListeAvis = JSON.parse(localStorage.getItem('liste des avis'));

    return (
        <div className="Avis">
            Avis des clients
            <AjouterAvis />
            <div className="listeAvis">
                {updateListeAvis.map((avis, index) => (
                    <div className="avis" index={index}>
                        <div className="nomClientNote">
                            <h4>{avis.name}</h4>
                            <div>
                                {[...Array(5)].map((star, index) => {
                                    const ratingValue = avis.note;
                                    <FaStar
                                        value={ratingValue}
                                    />
                                    /*return (
                                        <div className="positionStarRating">
                                            <label>
                                                <input
                                                    type="radio"
                                                    name="note"
                                                    id="note"
                                                    value={ratingValue}
                                                //onChange={handleInputChange}
                                                //onClick={() => setRating(ratingValue)}
                                                />
                                            </label>
                                            <FaStar
                                                value={ratingValue} />
                                        </div>
                                    );*/
                                })}
                            </div>
                            <h4>{avis.note}/5</h4>
                        </div>
                        <p>{avis.message}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Avis;
