import React from 'react';
import StarRating from './StarRating';
import { useState } from 'react';

const Avis = () => {

    const [donnerAvis, setDonnerAvis] = useState(false);

    const envoyerAvis = () => {
        alert('avis envoyez rip')
    }

    return (
        <div className="Avis">
            Avis des clients
            <button className="donnerAvis" onClick={() => setDonnerAvis(true)}>Donner son avis</button>
            {donnerAvis === false ?
                <div className="listeAvis">
                    <div className="nomClientNote">
                        <h4>Nom personne</h4>
                        <StarRating />
                    </div>
                    <p>paragraphe avis blablabla blablabla blablabla blablabla blablabla</p>
                </div>
                :
                <div>
                    <form onSubmit={envoyerAvis}>
                        <legend>Laissez votre avis</legend>

                        <input type="text" name="nom" id="nom" placeholder="Votre nom"></input>
                        <label htmlFor="nom"></label>

                        <textarea name="avis" id="avis" cols="150" rows="5" placeholder="Votre message"></textarea>
                    </form>
                    <button onClick={() => (envoyerAvis(), setDonnerAvis(false))}>Confimer</button>
                </div>
            }
        </div>
    );
};

export default Avis;
