import React, { useState } from 'react';
import ReactModal from 'react-modal';
import axios from 'axios';

const BoutonCreerUtilisateur = () => {

    const [isOpen, setIsOpen] = useState(false);
    const [newUser, setNewUser] = useState(getDefaultUser());

    function getDefaultUser() {
        return {
            email: "",
            password: "",
        }
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        setNewUser({
            ...newUser,
            [name]: value,
        })
    };

    const handleSubmit = async (e) => {
        console.log('ok ok')
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3001/CreateUser', newUser, {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            console.log('Succès:', response.data);
            setNewUser(getDefaultUser());
            setIsModalOpen(false);
        } catch (error) {
            console.error('Erreur:', error.response ? error.response.data : error.message);
        }
    }

    const handleIsOpen = () => {
        setIsOpen(true);
    }

    const handleIsClose = () => {
        setIsOpen(false);
    }

    return (
        <div>
            <button className="boutonCreerEmploye" onClick={openModal}>Créer compte employé</button>


            <input
                type="email"
                name="email"
                placeholder="Email"
                onChange={handleInputChange}
                value={newUser.email}
                required
            />

            <input
                type="password"
                name="password"
                placeholder="Mot de passe"
                onChange={handleInputChange}
                value={newUser.password}
                required
            />

            <div>
                <button type="submit">Créer</button>
                <button type="button" onClick={closeModal}>Annuler</button>
            </div>
        </div>
    );
};

export default BoutonCreerUtilisateur;
