import React from 'react';
import { useParams } from 'react-router-dom';
import Navigation from '../components/Navigation';
import { useState, useEffect } from 'react';

const DetailsVoiture = () => {

    const { id } = useParams();
    const [dataVoitures, setDataVoitures] = useState('');

    useEffect(() => {
        const voituresDansStorage = JSON.parse(localStorage.getItem('liste voitures'));
        console.log(voituresDansStorage)
        const selectedVoiture = voituresDansStorage.find(voiture => voiture.id === parseInt(id));

        if (selectedVoiture) {
            setDataVoitures(selectedVoiture);
        } else {
            console.error(`Voiture avec l'ID ${id} introuvable`)
        }
    }, [id]);

    return (
        <div>
            <Navigation />
            <p>prix: {dataVoitures.price}</p>

        </div>
    );
};

export default DetailsVoiture;
