import React from 'react';
import StarRating from './StarRating';
import { useState } from 'react';

const Avis = () => {

    const [donnerAvis, setDonnerAvis] = useState(false);

    return (
        <div className="Avis">
            Avis des clients
            <button className="donnerAvis" onClick={() => setDonnerAvis(true)}>Donner son avis</button>
            <div className="listeAvis">
                <div className="nomClientNote">
                    <h4>Nom personne</h4>
                    <StarRating />
                </div>
                <p>paragraphe avis blablabla blablabla blablabla blablabla blablabla</p>
            </div>
        </div>
    );
};

export default Avis;
