import React from 'react';
import Navigation from '../components/Navigation';
import BoutonAjoutVoitures from '../components/BoutonAjoutVoitures';
import Footer from '../components/Footer';
import BoutonSupprimerVoitures from '../components/BoutonSupprimerVoitures';
import BoutonApprouverAvis from '../components/BoutonApprouverAvis';
import BoutonSupprimerAvis from '../components/BoutonSupprimerAvis';

const DashboardEmploye = () => {

    const avisNonApprouve = JSON.parse(localStorage.getItem('liste des avis non approuvés'));
    console.log('dashboardEmployer', avisNonApprouve)

    return (
        <div>
            <Navigation />
            <h1>LE DASHBOARD EMPLOYE</h1>
            <div className="buttonDashboard">
                <BoutonAjoutVoitures />
                <BoutonSupprimerVoitures />
            </div>
            <div className="listeAvis">
                <h2>Liste des avis en attente de confirmation</h2>
                {avisNonApprouve.map((avis, index) => (
                    <div className="avis" index={index}>
                        <div className="nomClientNote">
                            <h4>{avis.name}</h4>
                            <h4>{avis.note}/5</h4>
                        </div>
                        <p>{avis.message}</p>
                        <div>
                            <BoutonApprouverAvis />
                            <BoutonSupprimerAvis />
                        </div>
                    </div>
                ))}
            </div>
            <Footer />
        </div>
    );
};

export default DashboardEmploye;
