import React from 'react';
import service1 from './image/garage-service-1.jpg'
import service2 from './image/garage-service-2.jpg'
import service3 from './image/garage-service-3.jpg'

const Services = () => {

    const listeServices = [
        { id: 1, name: "service1", content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Phasellus eu augue porta, commodo purus in", image: service1 },
        { id: 2, name: "service2", content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Phasellus eu augue porta, commodo purus in", image: service2 },
        { id: 3, name: "service3", content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Phasellus eu augue porta, commodo purus in", image: service3 }
    ]

    //const getListeVoitures = JSON.parse(localStorage.getItem('liste de voitures'));
    //console.log(getListeVoitures);

    const listeServicesString = JSON.stringify(listeServices);
    localStorage.setItem('liste de services', listeServicesString);

    const updateListeServices = JSON.parse(localStorage.getItem('liste de services'));
    console.log('page service ', updateListeServices)

    //const updateListeServices = localStorage.getItem('liste de services2');


    return (
        <div>
            {updateListeServices === null ?
                <div>
                    <h1 className="titreServices">Nos services</h1>
                    {listeServices.map((service, index) => (
                        <div className="services" index={index}>
                            <p className="paragrapheService">{service.content}</p>
                            <img src={service.image} className="serviceGarage" />
                        </div>
                    ))}
                </div>
                :
                <div>
                    <h1 className="titreServices">Nos services</h1>
                    {updateListeServices.map((service, index) => (
                        <div className="services" index={index}>
                            <p className="paragrapheService">{service.content}</p>
                            <img src={service.image} className="serviceGarage" />
                        </div>
                    ))}
                </div>
            }
        </div>
    );
};

export default Services;
