import React from 'react';
import ReactModal from 'react-modal';
import { useState, useEffect } from 'react';

const BouttonSupprimerServices = () => {

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectService, setSelectService] = useState('');

    const getListeServicesUpdate = JSON.parse(localStorage.getItem('liste de services update'));

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);




    /*const filterServiceName = Array.from(
        new Set(getListeServicesUpdate.map(service => service.name))
    );

    const supprimerService = (e) => {
        e.preventDefault();

        console.log(filterServiceName);

        const filterListeServices = filterServiceName.filter(name => name !== selectService);
        console.log('on supprime ?', filterListeServices);

    };*/



    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    console.log('boutton supprimer 2', selectService);

    const supprimerService = (e) => {
        e.preventDefault();

        console.log(filterServiceName);

        const filterListeServices = filterServiceName.filter(name => name !== selectService);
        console.log('on supprime ?', filterListeServices);

    };

    return (
        <div>
            <button className="buttonSupprimerService" onClick={openModal}>Supprimer un service</button>
            <ReactModal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
            >
                <form className="supprimerService" onSubmit={supprimerService}>
                    <legend>Quel service souhaitez-vous supprimer ?</legend>
                    <select
                        name="servicesSuppression"
                        id="servicesSuppression"
                        value={selectService}
                        onChange={(e) => setSelectService(e.target.value)}
                        required
                    >
                        <option value="">Veuillez choisir le service à supprimer</option>
                        {getListeServicesUpdate.map((service, index) => (
                            <option key={index} value={service.name}>
                                {service.name}
                            </option>
                        ))}
                    </select>
                    <div>
                        <button className="bouttonSupprimer" type="submit">Supprimer</button>
                        <button className="bouttonAnnulerSupr" onClick={closeModal}>Annuler</button>
                    </div>
                </form>

            </ReactModal>
        </div>
    );
};

export default BouttonSupprimerServices;
