import React from 'react';
import StarRating from './StarRating';
import { useState } from 'react';

const AjouterAvis = () => {

    const [newAvis, setNewAvis] = useState(getDefaultAvis());
    const [isModalOpen, setIsModalOpen] = useState(false);

    function getDefaultAvis() {
        return {
            name: "Tibo",
            note: 4,
            message: "Lorem ipsum"
        };
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        const newValue = value;

        setNewAvis({
            ...newAvis,
            [name]: newValue,
        });
    };

    const handleAddAvis = () => {
        const getListeAvisUpdate = JSON.parse(localStorage.getItem('liste des avis')) || [];
        const updateAvis = [...getListeAvisUpdate, newAvis];
        localStorage.setItem('liste des avis', JSON.stringify(updateAvis));
        setNewAvis(getDefaultAvis());
        alert('Votre avis à bien été envoyé');
    }

    return (
        <div>
            <form onSubmit={handleAddAvis}>
                <legend>Laissez votre avis</legend>

                <input
                    type="text"
                    name="name"
                    id="name"
                    placeholder="Votre nom"
                    onChange={handleInputChange}
                    required
                />
                <label htmlFor="name"></label>

                <p>Veuilez saisir votre note</p>
                <StarRating />

                <textarea
                    name="message"
                    id="message"
                    cols="150"
                    rows="5"
                    placeholder="Votre message"
                    onChange={handleInputChange}
                />
            </form>
            <button onClick={() => (envoyerAvis(), setDonnerAvis(false))}>Confimer</button>
        </div>
    );
};

export default AjouterAvis;
