import React from 'react';
import { useEffect, useState } from 'react';
import axios from 'axios';
import ReactModal from 'react-modal';

const BoutonSupprimerEmploye = () => {

    const [isOpen, setIsOpen] = useState(false);
    const [data, setData] = useState([]);

    const loadData = async () => {
        const response = await axios.get('http://localhost:3001/api/get')
        setData(response.data);
    }

    useEffect(() => {
        loadData();
    }, []);

    const handleDelete = (email) => {
        if (window.confirm("Êtes-vous sûr de vouloir supprimer définitivement ce compte employé ?")) {
            axios.delete(`http://localhost:3001/api/remove/${email}`);
            setTimeout(() => loadData(), 500);
        }
    }

    console.log('data', data)

    return (
        <div>
            <button className="boutonSupprimerEmploye" onClick={openModal}>Supprimer compte employé</button>

            <ReactModal isOpen={isModalOpen} onRequestClose={closeModal}>
                <div>
                    LISTE DES EMPLOYES
                    <div>
                        {data.map((employe, index) => (
                            <div index={index}>
                                <p>employé : {employe.email}</p>
                                <button onClick={() => handleDelete(employe.email)}>Supprimer</button>
                            </div>
                        ))}
                    </div>
                </div>
            </ReactModal>
        </div>
    );
};

export default BoutonSupprimerEmploye;
