import React from 'react';
import Navigation from '../components/Navigation';
import { useEffect, useState } from 'react';
import ReactModal from 'react-modal';

const DashboardAdmin = () => {

    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div>
            <Navigation />
            <h1>LE DASHBOARD ADMIN</h1>
            <div>
                <button className="buttonService" onClick={openModal}>Ajouter un service</button>
                <ReactModal
                    isOpen={isModalOpen}
                    onRequestClose={closeModal}
                >
                    <form>
                        <legend>Ajouter un service</legend>
                        <input type="text" name="intituleService" id="intituleService" placeholder="L'intitulé du service" required />
                        <label htmlFor="intituleService"></label>

                        <textarea name="question" id="question" cols="100" rows="10" placeholder="Votre message" defaultValue={message} />
                    </form>

                </ReactModal>
                <button className="buttonService">Supprimer un service</button>
            </div>
        </div>
    );
};

export default DashboardAdmin;
