import React from 'react';
import { useParams } from 'react-router-dom';
import Navigation from '../components/Navigation';
import { useState, useEffect } from 'react';
import ReactModal from 'react-modal';

const DetailsVoiture = () => {

    const { id } = useParams();
    const [dataVoitures, setDataVoitures] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isModalOpen2, setIsModalOpen2] = useState(false);
    const [message, setMessage] = useState("");

    const idNumber = Number(id);

    useEffect(() => {
        const voituresExisting = JSON.parse(localStorage.getItem('liste de voitures')) || [];

        const selectedVoiture = voituresExisting.find(voiture => voiture.id === idNumber);

        if (selectedVoiture) {
            setDataVoitures(selectedVoiture);
        } else {
            console.error(`La voiture avec l'ID ${idNumber} est introuvable`)
        }
    }, [idNumber]);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    const openModal2 = () => {
        setIsModalOpen2(true);
        setMessage(`Je serais intéresser par la voiture ${dataVoitures.brand}`);
    };

    const closeModal2 = () => {
        setIsModalOpen2(false);
    };

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    const envoieMessage = (e) => {
        e.preventDefault();
        console.log('Envoie du message réussis')
    }

    return (
        <div>
            <Navigation />
            <h2 className="titreAnnonce">Annonce pour la voiture {dataVoitures.brand}</h2>
            <div className="conteneurDetailVoiture">
                <img className="imageDetailVoiture" src={dataVoitures.image} />
                <div>
                    <p>Kilométrage : {dataVoitures.km} km</p>
                    <p>Prix : {dataVoitures.price} €</p>
                    <p>Mise en circulation en : {dataVoitures.yearsCirculation}</p>
                    <span>Intéressé ? contactez-nous par message ou par téléphone</span>
                    <div className="boutonContactDetailVoiture">
                        <button className="boutonContactDetailVoitureMessage" onClick={openModal2}>Par message</button>
                        <ReactModal
                            isOpen={isModalOpen2}
                            onRequestClose={closeModal2}
                            className="myModal2"
                        >
                            <form id="formulaireContact" onSubmit={envoieMessage}>
                                <legend>Laissez-nous votre message</legend>
                                <input type="text" name="nom" id="nom" placeholder="Votre nom"></input>
                                <label htmlFor="nom"></label>

                                <input type="text" name="prenom" id="prenom" placeholder="Votre prénom"></input>
                                <label htmlFor="prenom"></label>

                                <input type="email" name="email" id="email" placeholder="Votre email"></input>
                                <label htmlFor="email"></label>

                                <input type="tel" name="phone" id="phone" placeholder="Votre numéro de téléphone"></input>
                                <label htmlFor="phone"></label>

                                <textarea name="question" id="question" cols="100" rows="10" placeholder="Votre message" defaultValue={message}>

                                </textarea>

                                <div className="boutonContactVoiture">
                                    <button type="submit" value="Envoyer" className="boutonContact" onClick={envoieMessage}>Envoyer</button>
                                    <button onClick={closeModal2} className="boutonContact">Annuler</button>
                                </div>
                            </form>
                        </ReactModal>

                        <button className="boutonContactDetailVoitureTelephone" onClick={openModal}>Par téléphone</button>
                        <ReactModal
                            isOpen={isModalOpen}
                            onRequestClose={closeModal}
                            className="myModal"
                        >
                            <h3>Notre numéro de téléphone : </h3>
                            <p>06 06 06 06 06</p>
                            <button onClick={closeModal}>Fermer</button>
                        </ReactModal>
                    </div>
                </div>
            </div>

        </div >
    );
};

export default DetailsVoiture;
