import React from 'react';
import ReactModal from 'react-modal';
import { useState, useEffect } from 'react';

const Footer = () => {

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [date, setDate] = useState();
    const [creneau, setCreneau] = useState();
    const [heure, setHeure] = useState();

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    const dateRendezVous = new Date(date).getDay();
    console.log(dateRendezVous);

    if (dateRendezVous === 0) {
        console.log('Vous avec choisit une mauvaise date')
    }

    console.log('creneau', creaneau)

    return (
        <div className="Footer">
            <div className="horaireGarage">
                <table>
                    <div>
                        <tr>
                            <td>Lun :</td>
                            <td>08h45 - 12h00, 14h00 - 18h00</td>
                        </tr>
                        <tr>
                            <td>Mar :</td>
                            <td>08h45 - 12h00, 14h00 - 18h00</td>
                        </tr>
                    </div>
                    <div>
                        <tr>
                            <td>Mer :</td>
                            <td>08h45 - 12h00, 14h00 - 18h00</td>
                        </tr>
                        <tr>
                            <td>Jeu :</td>
                            <td>08h45 - 12h00, 14h00 - 18h00</td>
                        </tr>

                    </div>
                    <div>
                        <tr>
                            <td>Ven :</td>
                            <td>08h45 - 12h00, 14h00 - 18h00</td>
                        </tr>
                        <tr>
                            <td>Sam :</td>
                            <td>08h45 - 12h00</td>
                        </tr>
                    </div>
                    <div>
                        <tr>
                            <td>Dim :</td>
                            <td>Fermé</td>
                        </tr>
                    </div>
                    <div>
                        <button onClick={openModal}>Je prends rendez-vous</button>
                    </div>
                </table>
            </div>
            <ReactModal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
            >
                <form>
                    <legend>
                        Votre rendez-vous
                    </legend>
                    <h1>SELECTED DATE : {date}</h1>
                    <input type="text" name="nom" id="nom" placeholder="Votre nom"></input>
                    <label htmlFor="nom"></label>

                    <input type="text" name="prenom" id="prenom" placeholder="Votre prénom"></input>
                    <label htmlFor="prenom"></label>

                    <input type="email" name="email" id="email" placeholder="Votre email"></input>
                    <label htmlFor="email"></label>

                    <input type="tel" name="phone" id="phone" placeholder="Votre numéro de téléphone"></input>
                    <label htmlFor="phone"></label>

                    <input type="date" id="date" name="date" min="2024-01-01" max="2024-12-31" onChange={e => setDate(e.target.value)}></input>
                    <label htmlFor="date"></label>

                    <label htmlFor="creneau" id="creneau">Choisissez votre créneau horaire souhaité
                        <input type="radio" id="creneau" name="creneau" onChange={(e) => setCreneau("Matin")}></input><span>Matin</span>
                        <input type="radio" id="creneau" name="creneau" onChange={(e) => setCreneau("Après-midi")}></input><span>Après-midi</span>
                    </label>

                    <select
                        name="heureRendezVous"
                        id="heureRendezVous"
                        //required
                        value={heure}
                        onChange={(e) => setHeure(e.target.value)}
                    >
                        <option value="">Veuillez choisir votre créneau horaire</option>

                    </select>

                    <label for="appt">Select a time:</label>
                    <input type="time" id="appt" name="appt"></input>
                </form>

            </ReactModal>
        </div>
    );
};

export default Footer;
