import React from 'react';
import { useState } from 'react';

const ApprouverOuSupprimerAvis = () => {

    //const [name, setName] = useState('');

    const avisNonApprouve = JSON.parse(localStorage.getItem('liste des avis non approuvés'));
    console.log('dashboardEmployer', avisNonApprouve)

    const supprimerAvis = (e) => {
        e.preventDefault();
        alert(`le commentaire de ${avis.name} à été supprimer`)
    }

    return (
        <div className="listeAvis">
            <h2>Liste des avis en attente de confirmation</h2>
            {avisNonApprouve.map((avis, index) => (
                <div className="avis" index={index}>
                    <div className="nomClientNote">
                        <h4>{avis.name}</h4>
                        <h4>{avis.note}/5</h4>
                    </div>
                    <p>{avis.message}</p>
                    <button>Approuver</button>
                    <button onClick={alert(`le commentaire de ${avis.name} à été supprimer`)}>Supprimer</button>
                </div>
            ))}
        </div>
    );
};

export default ApprouverOuSupprimerAvis;
