import React from 'react';
import { useEffect, useState } from 'react';
import axios from 'axios';
import ReactModal from 'react-modal';

const BoutonSupprimerEmploye = () => {

    const [listeEmployes, setListeEmployes] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectEmployeEmail, setSelectEmployeEmail] = useState('');

    const [data, setData] = useState([]);

    const loadData = async () => {
        const response = await axios.get('http://localhost:3001/api/get')
        setData(response.data);
    }

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    useEffect(() => {
        const getEmployes = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/get', {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                const employes = JSON.stringify(response.data)
                setListeEmployes(Array.from(new Set(JSON.parse(employes))));
                console.log('Succès:', response.data);
            } catch (error) {
                console.error('Erreur:', error.response ? error.response.data : error.message);
            }
        }
        getEmployes();

        //loadData();
    }, []);

    const tableauEmployes = Array.from(
        new Set(listeEmployes)
    );

    const handleDelete = (e) => {
        e.preventDefault();
        if (window.confirm("êtes-vous sûr de vouloir supprimer définitivement ce compte employé ?")) {
            axios.delete(`http://localhost:3001/api/remove/${selectEmployeEmail}`);
            setTimeout(() => loadData(), 500);
        }
    }

    console.log(selectEmployeEmail)

    return (
        <div>
            <button className="boutonSupprimerEmploye" onClick={openModal}>Supprimer compte employé</button>

            <ReactModal isOpen={isModalOpen} onRequestClose={closeModal}>
                <form onSubmit={handleDelete}>
                    <legend>Supprimer un compte employé</legend>

                    <select
                        name="compteEmployeSuppression"
                        id="compteEmployeSuppression"
                        value={selectEmployeEmail}
                        onChange={(e) => setSelectEmployeEmail(e.target.value)}
                        required
                    >
                        <option value="">Veuillez choisir le compte employé à supprimer</option>
                        {tableauEmployes.map((employe, index) => (
                            <option key={index} value={employe.email}>
                                Employé : {employe.email}
                            </option>
                        ))}
                    </select>

                    <div>
                        <button type="submit">Supprimer</button>
                        <button type="button" onClick={closeModal}>Annuler</button>
                    </div>
                </form>
            </ReactModal>
        </div>
    );
};

export default BoutonSupprimerEmploye;
