import React from 'react';
import Navigation from '../components/Navigation';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Footer from '../components/Footer';

const SeConnecter = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [estConnecte, setEstConnecte] = useState(false);

    const navigate = useNavigate();

    const connexion = (e) => {
        e.preventDefault();
        localStorage.setItem('est connecté', estConnecte);
        console.log('bravo vous êtes connecté', email, password);
        localStorage.setItem('email user', email);
        localStorage.setItem('password user', password);
        const userConnecte = localStorage.getItem('est connecté');
        const emailUser = localStorage.getItem('email user');
        const passwordUser = localStorage.getItem('password user');
        //console.log(userConnecte);

        if ((emailUser === "vincent@vincent" && passwordUser === "parrot") ||
            (emailUser === "employe@employe" && passwordUser === "employe")) {
            navigate("/");
            window.location.reload();
        } else {
            alert('Vous n\'êtes pas autorisé à utiliser l\'espace pro');
        }
    }


    const deconnexion = (e) => {
        e.preventDefault();
        console.log('test2', localStorage.getItem('est connecté'));
        localStorage.removeItem('est connecté');
        localStorage.removeItem('email user');
        localStorage.removeItem('password user');
        navigate("/");
        window.location.reload();
    }

    const userConnexion = () => {
        setEstConnecte(true);
        console.log(estConnecte);
    }

    return (
        <div>
            <Navigation />
            <h1>Connexion espace professionnel</h1>
            <div className="conteneurConnexion">
                <form className="formulaireConnexion">
                    <legend>Votre compte</legend>

                    <input
                        type="email"
                        name="email"
                        id="email"
                        placeholder="votre email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <label htmlFor="email"></label>

                    <input
                        type="password"
                        name="password"
                        id="password"
                        placeholder="votre mot de passe"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <label htmlFor="password"></label>

                    {estConnecte === false ?
                        <button
                            onClick={userConnexion}
                        >
                            Se connecter
                        </button>
                        :
                        <button
                            onClick={
                                (() => setEstConnecte(false),
                                    deconnexion)}
                        >
                            Se deconnecter
                        </button>
                    }
                </form>

            </div>
            <Footer />
        </div>
    );
};

export default SeConnecter;
