import React from 'react';
import Navigation from '../components/Navigation';
import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Footer from '../components/Footer';
import axios from 'axios';

const SeConnecter = () => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [listeEmployes, setListeEmployes] = useState('');

    /*const listeEmployesEmail = Array.from(
        new Set(listeEmployes.map(employe => employe.email))
    );*/

    const navigate = useNavigate();

    const connexion = (e) => {
        e.preventDefault();
        console.log('bravo vous êtes connecté', email, password);
        localStorage.setItem('email user', email);
        localStorage.setItem('password user', password);
        const emailUser = localStorage.getItem('email user');
        const passwordUser = localStorage.getItem('password user');

        if ((emailUser === "vincent@vincent" && passwordUser === "parrot") ||
            (emailUser === "employe@employe" && passwordUser === "employe")) {
            navigate("/");
            window.location.reload();
        } else {
            alert('Vous n\'êtes pas autorisé à utiliser l\'espace pro');
        }
    }

    const deconnexion = (e) => {
        e.preventDefault();
        localStorage.removeItem('email user');
        localStorage.removeItem('password user');
        navigate("/");
        window.location.reload();
    }

    useEffect(() => {
        try {
            const response = axios.get('http://localhost:3001/api/get', {
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            const employes = JSON.stringify(response.data)
            setListeEmployes(Array.from(new Set(JSON.parse(employes))));
            console.log('Succès:', response.data);
        } catch (error) {
            console.error('Erreur:', error.response ? error.response.data : error.message);
        }
    }, [])

    const getEmployes2 = () => {
        console.log(listeEmployes);
    }


    const tableau = Array.from(
        new Set(listeEmployes)
    );


    return (
        <div>
            <Navigation />
            <button onClick={getEmployes2}>Get les employés2</button>
            {tableau.map((employe, index) => (
                <p index={index}>{employe.email}</p>
            ))}
            <h1>Espace professionnel</h1>
            <div className="conteneurConnexion">
                <form className="formulaireConnexion">
                    {localStorage.getItem('email user') !== null && localStorage.getItem('password user') !== null ?
                        <>
                            <legend>Vous êtes connecté en tant que {localStorage.getItem('email user')}</legend>
                            <button onClick={deconnexion}>
                                Se déconnecter
                            </button>
                        </>
                        :
                        <div>
                            <input
                                type="email"
                                name="email"
                                id="email"
                                placeholder="votre email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                            <label htmlFor="email"></label>

                            <input
                                type="password"
                                name="password"
                                id="password"
                                placeholder="votre mot de passe"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                            <label htmlFor="password"></label>

                            <div className="boutonConnexion">
                                <button onClick={connexion}>
                                    Se connecter
                                </button>
                            </div>
                        </div>
                    }
                </form>
            </div>
            <Footer />
        </div>
    );
};

export default SeConnecter;
