import React from 'react';
import AjouterAvis from './AjouterAvis';

const Avis = () => {

    const updateListeAvis = JSON.parse(localStorage.getItem('liste des avis'));

    return (
        <div className="Avis">
            Avis des clients
            <AjouterAvis />
            <div className="listeAvis">
                {updateListeAvis.map((avis, index) => (
                    <div className="avis" index={index}>
                        <div className="nomClientNote">
                            <h4>{avis.name}</h4>
                            <div>
                                {[...Array(5)].map((star, index) => {
                                    const ratingValue = index + 1;
                                    return (
                                        <div className="positionStarRating">
                                            <label>
                                                <input
                                                    type="radio"
                                                    name="note"
                                                    id="note"
                                                    value={ratingValue}
                                                    onChange={handleInputChange}
                                                    onClick={() => setRating(ratingValue)}
                                                />
                                                <FaStar
                                                    className="star"
                                                    color={ratingValue <= (hover || rating) ? "#ffc107" : "#e4e5e9"}
                                                    size={40}
                                                    onMouseEnter={() => setHover(ratingValue)}
                                                    onMouseLeave={() => setHover(null)}
                                                />
                                            </label>
                                        </div>
                                    );
                                })}
                                <p>la note est {rating}</p>
                            </div>
                            <h4>{avis.note}/5</h4>
                        </div>
                        <p>{avis.message}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Avis;
