import React from 'react';
import Navigation from '../components/Navigation';
import { useEffect, useState } from 'react';
import ReactModal from 'react-modal';

const DashboardAdmin = () => {

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [intituleService, setIntituleService] = useState('');
    const [contentService, setContentService] = useState('');
    const [imageService, setImageService] = useState('');

    const [updateServices, setUpdateServices] = useState([]);

    const [addNewService, setAddNewService] = useState('');

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };


    let newService = {
        name: intituleService,
        content: contentService,
        image: imageService,
    };

    const getListeServices = JSON.parse(localStorage.getItem('liste de services'));

    getListeServices.push(newService);

    console.log('nouveau service', getListeServices);

    /*
    useEffect(() => {
        const getListeServices = JSON.parse(localStorage.getItem('liste de services'));
        setUpdateServices(getListeServices);
    }, []);*/


    /*
    useEffect(() => {
        getListeServices.push(newService);
        setUpdateServices(getListeServices);
        console.log('nouveau service', updateServices);
    }, []);*/

    const ajoutService = () => {
        setUpdateServices(getListeServices.push(newService));
    }

    const handleInputChange = (e) => {
        const { name, value, type } = e.target;

        const newValue = type === "range" ? Number(value) : value;

        setAddNewService({
            ...addNewService,
            [name]: newValue,
        });
    };

    console.log(updateServices);

    return (
        <div>
            <Navigation />
            <h1>LE DASHBOARD ADMIN</h1>
            <div>
                <button className="buttonService" onClick={openModal}>Ajouter un service</button>
                <ReactModal
                    isOpen={isModalOpen}
                    onRequestClose={closeModal}
                >
                    <form className="ajoutService" onSubmit={ajoutService}>
                        <legend>Ajouter un service</legend>
                        <input
                            type="text"
                            name="intituleService"
                            id="intituleService"
                            placeholder="L'intitulÃ© du service"
                            value={intituleService}
                            onChange={(e) => setIntituleService(e.target.value)}
                            required
                        />
                        <label htmlFor="intituleService"></label>

                        <textarea
                            name="explicationService"
                            id="explicationService"
                            cols="100"
                            rows="5"
                            placeholder="Explication du service"
                            value={contentService}
                            onChange={(e) => setContentService(e.target.value)}
                            required
                        />
                        <label htmlFor="explicationService"></label>


                        <input
                            type="file"
                            name="uploadImage"
                            id="uploadImage"
                            value={imageService}
                            onChange={(e) => setImageService(e.target.value)}
                            required
                        />
                        <label htmlFor="uploadImage"></label>
                        <div>
                            <button className="buttonAjoutService" type="submit">Ajouter service</button>
                            <button className="buttonAnnulerService" onClick={closeModal}>Annuler</button>
                        </div>
                    </form>
                </ReactModal>
                <button className="buttonService">Supprimer un service</button>
            </div>
        </div>
    );
};

export default DashboardAdmin;
