import React from 'react';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import Accueil from './pages/Accueil';
import Contact from './pages/Contact';
import "./App.css";
import DetailsVoiture from './pages/DetailsVoiture';
import SeConnecter from './pages/SeConnecter';
import Occasions from './pages/Occasions';
import DashboardAdmin from './pages/DashboardAdmin';
import Fermeture from './pages/Fermeture';
import DashboardEmploye from './pages/DashboardEmploye';
import { useEffect, useState } from 'react';

const App = () => {

  const emailUser = localStorage.getItem('email user');
  const ouvertOuFermer = localStorage.getItem('ouvert ou fermer');
  const passwordUser = localStorage.getItem('password user');

  /* const listeAvis = [
     { name: "Martin", note: 5, message: "Lorem ipsum dolor sit amet" },
     { name: "Jeanne", note: 3, message: "Lorem ipsum dolor sit amet" },
     { name: "Claude", note: 4, message: "Lorem ipsum dolor sit amet" },
     { name: "Lisa", note: 5, message: "Lorem ipsum dolor sit amet" }
   ]*/

  const [avisNonApprouve, setAvisNonApprouve] = useState([]);

  //const listeAvisNonApprouve = [];

  useEffect(() => {
    //localStorage.setItem('liste des avis', JSON.stringify(listeAvis));

    localStorage.setItem('liste des avis non approuvés', JSON.stringify(avisNonApprouve));
  }, [avisNonApprouve]);

  useEffect(() => {
    const data = localStorage.getItem('liste des avis non approuvés');
    if (data !== null) setAvisNonApprouve(JSON.parse(data))
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        {emailUser === null && passwordUser === null && ouvertOuFermer === 'fermer' ?
          < Route path="/" element={<Fermeture />} />
          :
          <Route path="/" element={<Accueil />} />
        }
        <Route path="voiture/:id" element={<DetailsVoiture />} />
        <Route path="/contact" element={<Contact />} />
        <Route path="voiture/:id/contact" element={<Contact />} />
        <Route path="/occasions" element={<Occasions />} />
        <Route path="/connexion" element={<SeConnecter />} />
        <Route path="/dashboard-admin" element={<DashboardAdmin />} />
        <Route path="/dashboard-employe" element={<DashboardEmploye />} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
