import React from 'react';
import service1 from './image/garage-service-1.jpg'
import service2 from './image/garage-service-2.jpg'
import service3 from './image/garage-service-3.jpg'

const Services = () => {

    const listeServices = [
        { name: "service1", content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Phasellus eu augue porta, commodo purus in", image: service1 },
        { name: "service2", content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Phasellus eu augue porta, commodo purus in", image: service2 },
        { name: "service3", content: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Phasellus eu augue porta, commodo purus in", image: service3 }
    ]

    //const getListeVoitures = JSON.parse(localStorage.getItem('liste de voitures'));
    //console.log(getListeVoitures);

    const listeServicesString = JSON.stringify(listeServices);
    localStorage.setItem('liste de services', listeServicesString);

    //const updateListeServices = JSON.parse(localStorage.getItem('liste de services'));

    const updateListeServices = JSON.parse(localStorage.getItem('liste de services update'));
    console.log('page service', updateListeServices);

    const update2 = [...listeServices];
    console.log(update2)

    if (updateListeServices !== null) {
        update2 = [...updateListeServices];
    }

    const clear = () => {
        localStorage.removeItem('liste de services');
        localStorage.removeItem('liste de services update');
    }

    return (
        <div>
            {updateListeServices !== null ?
                <div>
                    <h1 className="titreServices">Nos services</h1>
                    {update2.map((service, index) => (
                        <div className="services" index={index}>
                            <p className="paragrapheService">{service.content}</p>
                            <img src={service.image} className="serviceGarage" />
                        </div>
                    ))}
                </div>
                :
                <div>
                    <h1 className="titreServices">Nos services</h1>
                    {listeServices.map((service, index) => (
                        <div className="services" index={index}>
                            <p className="paragrapheService">{service.content}</p>
                            <img src={service.image} className="serviceGarage" />
                        </div>
                    ))}
                </div>
            }



            {/*
            {updateListeServices === null ?
                <div>
                    <h1 className="titreServices">Nos services</h1>
                    {listeServices.map((service, index) => (
                        <div className="services" index={index}>
                            <p className="paragrapheService">{service.content}</p>
                            <img src={service.image} className="serviceGarage" />
                        </div>
                    ))}
                </div>
                :
                <div>
                    <h1 className="titreServices">Nos services</h1>
                    {update2.map((service, index) => (
                        <div className="services" index={index}>
                            <p className="paragrapheService">{service.content}</p>
                            <img src={service.image} className="serviceGarage" />
                        </div>
                    ))}
                </div>
            }
        */}
            <button onClick={clear}>clear </button>
        </div>

    );
};

export default Services;
