import React from 'react';

const AjouterAvis = () => {
    return (
        <div>
            <div>
                <form>
                    <legend>Laissez votre avis</legend>

                    <input
                        type="text"
                        name="nom"
                        id="nom"
                        placeholder="Votre nom"
                        value={nom}
                        onChange={(e) => setNom(e.target.value)}
                    />
                    <label htmlFor="nom"></label>

                    <p>Veuilez saisir votre note</p>
                    <StarRating />

                    <textarea
                        name="avis"
                        id="avis"
                        cols="150"
                        rows="5"
                        placeholder="Votre message"
                        value={avis}
                        onChange={(e) => setAvis(e.target.value)}
                    />
                </form>
                <button onClick={() => (envoyerAvis(), setDonnerAvis(false))}>Confimer</button>
            </div>
        </div>
    );
};

export default AjouterAvis;
