import React from 'react';
import { useState } from 'react';

const FiltreVoiture = ({ listeVoitures }) => {

    const [kilometrage, setKilometrage] = useState();

    const filterKilometrage = Array.from(
        new Set(listeVoitures.map(voiture => voiture.km))
    )

    const minKilometrage = Math.min(...filterKilometrage);

    const maxKilometrage = Math.max(...filterKilometrage);

    const updateKilometrage = (e) => {
        const { name, value, type } = e.target;

        const newValue = type === "range" ? Number(value) : value;

        setKilometrage({
            [name]: newValue,
        });
    };

    return (
        <div className="FiltreVoiture">
            <form>
                <label htmlFor="Kilometrage">Kilométrage {kilometrage} </label>
                <input
                    type="range"
                    name="kilometrage"
                    min={minKilometrage}
                    max={maxKilometrage}
                    Value={kilometrage}
                    onChange={updateKilometrage}
                />
                {/*<span>{minKilometrage} - {kilometrage}</span>*/}
            </form>

        </div>
    );
};

export default FiltreVoiture;
