import React from 'react';
import AjouterAvis from './AjouterAvis';

const Avis = () => {

    const updateListeAvis = JSON.stringify(localStorage.getItem('liste des avis'));

    return (
        <div className="Avis">
            Avis des clients
            <AjouterAvis />
            <div className="listeAvis">
                {updateListeAvis.map((avis, index) => (
                    <div className="avis" index={index}>
                        <div className="nomClientNote">
                            <h4>{avis.name}</h4>
                            <h4>{avis.note}/5</h4>
                        </div>
                        <p>{avis.message}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Avis;
