import React from 'react';
import Navigation from '../components/Navigation';
import { useEffect, useState } from 'react';
import ReactModal from 'react-modal';
import serviceDefault from '../components/image/garage-service-default.jpg'

const DashboardAdmin = () => {

    const [isModalOpen, setIsModalOpen] = useState(false);
    /*
    const [intituleService, setIntituleService] = useState('');
    const [contentService, setContentService] = useState('');
    const [imageService, setImageService] = useState('');*/

    const [services, setServices] = useState([]);

    const [newServices, setNewServices] = useState(getDefaultService());

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    useEffect(() => {
        const getListeServices = JSON.parse(localStorage.getItem('liste de services')) || [];
        setServices(getListeServices);
    }, []);

    function getNextServiceId() {
        const getListeServices = JSON.parse(localStorage.getItem('liste de services')) || [];
        const lastService = getListeServices[getListeServices.length - 1];
        return lastService ? lastService.id + 1 : 1;
    }

    function getDefaultService() {
        return {
            name: "service",
            content: "lorem ipsum",
            image: serviceDefault,
        };
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        setNewServices({
            ...newServices,
            [name]: newValue,
        });
    };

    const handleAddService = () => {
        // Récupérer les données du localstorage
        const getListeServices = JSON.parse(localStorage.getItem('liste de services')) || [];
        // les services mises à jour
        const updateServices = [...getListeServices, newServices];
        // Mettre à jour les services dans le localstorage
        localStorage.setItem('maj services', JSON.stringify(updateServices));
        //mettre à jour l'affichage
        setServices(updateServices);

        //reprendre la valeur par défaut
        setNewServices(getDefaultService());
    }


    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };


    /*let newService = {
        name: intituleService,
        content: contentService,
        image: imageService,
    };*/


    //getListeServices.push(newService);

    //console.log('nouveau service', getListeServices);

    /*
    useEffect(() => {
        const getListeServices = JSON.parse(localStorage.getItem('liste de services'));
        setUpdateServices(getListeServices);
    }, []);*/


    /*
    useEffect(() => {
        getListeServices.push(newService);
        setUpdateServices(getListeServices);
        console.log('nouveau service', updateServices);
    }, []);*/

    /*
    const ajoutService = () => {
        setUpdateServices(getListeServices.push(newService));
    }
    console.log(updateServices);*/

    return (
        <div>
            <Navigation />
            <h1>LE DASHBOARD ADMIN</h1>
            <div>
                <button className="buttonService" onClick={openModal}>Ajouter un service</button>
                <ReactModal
                    isOpen={isModalOpen}
                    onRequestClose={closeModal}
                >
                    <form className="ajoutService" onSubmit={handleInputChange}>
                        <legend>Ajouter un service</legend>
                        <input
                            type="text"
                            name="intituleService"
                            id="intituleService"
                            placeholder="L'intitulé du service"

                            onChange={handleInputChange}
                            required
                        />
                        <label htmlFor="intituleService"></label>

                        <textarea
                            name="explicationService"
                            id="explicationService"
                            cols="100"
                            rows="5"
                            placeholder="Explication du service"

                            onChange={handleInputChange}
                            required
                        />
                        <label htmlFor="explicationService"></label>


                        <input
                            type="file"
                            name="uploadImage"
                            id="uploadImage"

                            onChange={handleInputChange}
                            required
                        />
                        <label htmlFor="uploadImage"></label>
                        <div>
                            <button className="buttonAjoutService" type="submit">Ajouter service</button>
                            <button className="buttonAnnulerService" onClick={closeModal}>Annuler</button>
                        </div>
                    </form>
                </ReactModal>
                <button className="buttonService">Supprimer un service</button>
            </div>
        </div>
    );
};

export default DashboardAdmin;
