import React from 'react';
import { useEffect, useState } from 'react';
import axios from 'axios';

const BoutonSupprimerEmploye = () => {

    const [listeEmployes, setListeEmployes] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    useEffect(() => {
        const getEmployes = async () => {
            try {
                const response = await axios.get('http://localhost:3001/api/get', {
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                const employes = JSON.stringify(response.data)
                setListeEmployes(Array.from(new Set(JSON.parse(employes))));
                console.log('Succès:', response.data);
            } catch (error) {
                console.error('Erreur:', error.response ? error.response.data : error.message);
            }
        }
        getEmployes();
    }, []);

    const tableauEmployes = Array.from(
        new Set(listeEmployes)
    );

    const handleSubmit = async (e) => {
        console.log('ok ok')
        e.preventDefault();
        setIsModalOpen(false);
    }

    return (
        <div>
            <button className="boutonSupprimerEmploye" onClick={openModal}>Supprimer compte employé</button>

            <ReactModal isOpen={isModalOpen} onRequestClose={closeModal}>
                <form onSubmit={handleSubmit}>
                    <legend>Créer un utilisateur</legend>
                    {/*
                    <input
                        type="text"
                        name="username"
                        placeholder="Nom d'utilisateur"
                        onChange={handleInputChange}
                        value={newUser.username}
                        required
                    />*/}

                    <input
                        type="email"
                        name="email"
                        placeholder="Email"
                        onChange={handleInputChange}
                        value={newUser.email}
                        required
                    />

                    <input
                        type="password"
                        name="password"
                        placeholder="Mot de passe"
                        onChange={handleInputChange}
                        value={newUser.password}
                        required
                    />

                    {/*<select
                        name="role"
                        onChange={handleInputChange}
                        value={newUser.role}
                    >
                        <option value="utilisateur">Utilisateur</option>
                        <option value="admin">Administrateur</option>
    </select>*/}

                    <div>
                        <button type="submit">Supprimer</button>
                        <button type="button" onClick={closeModal}>Annuler</button>
                    </div>
                </form>
            </ReactModal>
        </div>
    );
};

export default BoutonSupprimerEmploye;
