import React from 'react';
import { useEffect, useState } from 'react';
import ReactModal from 'react-modal';
import serviceDefault from '../components/image/garage-service-default.jpg'

const BoutonAjoutServices = () => {

    const [services, setServices] = useState([]);
    const [newServices, setNewServices] = useState(getDefaultService());
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    useEffect(() => {
        // Récupérer les données du localstorage
        const getListeServices = JSON.parse(localStorage.getItem('liste de services')) || [];
        //mettre les données dans la variable services
        setServices(getListeServices);
    }, []);

    function getNextServiceId() {
        const getListeServices = JSON.parse(localStorage.getItem('liste de services')) || [];
        const lastService = getListeServices[getListeServices.length - 1];
        return lastService ? lastService.id + 1 : 1;
    }

    function getDefaultService() {
        return {
            id: getNextServiceId(),
            name: "service test",
            content: "lorem ipsum",
            image: serviceDefault,
        };
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        const newValue = value;

        setNewServices({
            ...newServices,
            [name]: newValue,
        });
    };

    const handleAddService = () => {
        // les services mises à jour
        const updateServices = [...services, newServices];
        // Mettre à jour les services dans le localstorage
        localStorage.setItem('liste de services', JSON.stringify(updateServices));
        //mettre les données  à jour dans une autre variable
        const updateListeServices = localStorage.getItem('liste de services');
        //puis repasser ces donnée dans le localStorage avec une autre clé
        localStorage.setItem('liste de services update', updateListeServices);
        //mettre à jour l'affichage
        setServices(updateServices);
        //reprendre la valeur par défaut
        setNewServices(getDefaultService());
        //fermer la fenêtre modal
        setIsModalOpen(false);
    };

    const handleImageChange = (e) => {
        if (e.target.files && e.target.files[0]) {
            const img = e.target.files[0];
            setNewServices({
                ...newServices,
                image: URL.createObjectURL(img)
            })
        }
    }

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div>
            <button className="buttonService" onClick={openModal}>Ajouter un service</button>
            <ReactModal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
            >
                <form className="ajoutService" onSubmit={handleAddService}>
                    <legend>Ajouter un service</legend>
                    <input
                        type="text"
                        name="name"
                        id="name"
                        className="intituleService"
                        placeholder="L'intitulé du service"
                        onChange={handleInputChange}
                        required
                    />
                    <label htmlFor="intituleService"></label>

                    <textarea
                        name="content"
                        id="content"
                        cols="100"
                        rows="5"
                        placeholder="Explication du service"
                        onChange={handleInputChange}
                        required
                    />
                    <label htmlFor="explicationService"></label>

                    <input
                        type="file"
                        name="uploadImage"
                        id="uploadImage"
                        onChange={handleImageChange}
                    />
                    <label htmlFor="uploadImage"></label>
                    <div>
                        <button className="buttonAjoutService" type="submit">Ajouter service</button>
                        <button className="buttonAnnulerService" onClick={closeModal}>Annuler</button>
                    </div>
                </form>
            </ReactModal>
        </div>
    );
};

export default BoutonAjoutServices;
