import React from 'react';
import { useParams } from 'react-router-dom';
import Navigation from '../components/Navigation';
import { useState, useEffect } from 'react';
import ReactModal from 'react-modal';

const DetailsVoiture = () => {

    const { id } = useParams();
    const [dataVoitures, setDataVoitures] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        const voituresExisting = JSON.parse(localStorage.getItem('liste de voitures')) || [];

        const selectedVoiture = voituresExisting.find(voiture => voiture.id === id);

        if (selectedVoiture) {
            setDataVoitures(selectedVoiture);
        } else {
            console.error(`La voiture avec l'ID ${id} est introuvable`)
        }
    }, [id]);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);


    return (
        <div>
            <Navigation />
            <h2 className="titreAnnonce">Annonce pour la voiture {dataVoitures.brand}</h2>
            <div className="petitConteneurDetailVoiture">
                <img className="imageDetailVoiture" src={dataVoitures.image} />
                <div>
                    <p>Id : {dataVoitures.id}</p>
                    <p>Kilométrage : {dataVoitures.km} km</p>
                    <p>Prix : {dataVoitures.price} €</p>
                    <p>Mise en circulation en : {dataVoitures.yearsCirculation}</p>
                    <span>Intéressé ? contactez-nous par message ou par téléphone</span>
                    <div className="boutonContactVoiture">
                        <button className="boutonContact" onClick={openModal}>Par message</button>
                        <div className="modalTaille">
                            <ReactModal
                                isOpen={isModalOpen}
                                onRequestClose={closeModal}
                                className="myModal"
                                style={{
                                    overlay: {
                                        position: 'static',
                                        top: 'center',
                                        left: 'center',
                                        right: 'center',
                                        bottom: 'center',
                                        borderRadius: '4px',
                                    }
                                }}
                            >
                                <h2>Notre numéro de téléphone : </h2>
                                <h4>06 06 06 06 06</h4>
                                <button onClick={closeModal}>Fermer</button>
                            </ReactModal>
                        </div>
                        <button className="boutonContact">Par téléphone</button>
                    </div>
                </div>
            </div>

        </div >
    );
};

export default DetailsVoiture;
