import React from 'react';
import { useState, useEffect } from 'react';
import ReactModal from 'react-modal';

const BoutonSupprimerVoitures = () => {

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [selectVoitureId, setSelectVoitureId] = useState('');
    const [listeVoiture, setListeVoiture] = useState([]);

    const getListeVoiture = JSON.parse(localStorage.getItem('liste de voitures')) || [];

    useEffect(() => {
        ReactModal.setAppElement('body');
    }, []);

    useEffect(() => {
        setListeVoiture(getListeVoiture);

        //Trouver la voiture avec l'id correspondant
        const idVoiture = getListeVoiture.find(voiture => voiture.id === selectVoitureId)
    }, [selectVoitureId]);

    const supprimerService = (e) => {
        e.preventDefault();

        const updateIdVoiture = listeVoiture.filter(voiture => voiture.id !== selectVoitureId);

        localStorage.setItem('liste de voitures', JSON.stringify(updateIdVoiture));

        setListeVoiture(updateIdVoiture);

        alert(`La voiture avec l'ID numéro ${selectVoitureId} à bien été supprimer`);
    };

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div>
            <button className="buttonSupprimerVoiture" onClick={openModal}>Supprimer une voiture</button>
            <ReactModal
                isOpen={isModalOpen}
                onRequestClose={closeModal}
            >

            </ReactModal>
        </div>
    );
};

export default BoutonSupprimerVoitures;
